describe('the transform', function() {

  it('should do nothing if there is not a valid url in place', function() {
    var transformedText = transform('url foo.png', '/vendor/leaflet.draw/leaflet.draw.css');
    expect(transformedText).to.eql('url foo.png');

    transformedText = transform('url \'foo.png', '/vendor/leaflet.draw/leaflet.draw.css');
    expect(transformedText).to.eql('url \'foo.png');

    transformedText = transform('url(foo.png', '/vendor/leaflet.draw/leaflet.draw.css');
    expect(transformedText).to.eql('url(foo.png');
  });

  it('should ignore VML urls', function() {
    var transformedText = transform('url(#default#VML)', '/vendor/leaflet.draw/leaflet.draw.css');
    expect(transformedText).to.eql('url(#default#VML)');
  });

  it('should ignore VML urls in minified text', function() {
    var transformedText = transform('color:white;url(#default#VML)', '/vendor/leaflet.draw/leaflet.draw.css');
    expect(transformedText).to.eql('color:white;url(#default#VML)');
  });

  it('should ignore data uri urls', function() {
    var transformedText = transform('url(data:image/gif;base64,R0lGODlhGAAYACIiIiM)', '/vendor/foo/style.css');
    expect(transformedText).to.eql('url(data:image/gif;base64,R0lGODlhGAAYACIiIiM)');
  });

  it('should encode a PNG', function() {
    var transformedText = transform('background-image:url(images/sprite.png);', 'assets/style.css', null);
    expect(transformedText).to.eql('background-image:url(data:image/png;base64,);');
  });

  it('should encode a GIF', function() {
    var transformedText = transform('background-image:url(images/sprite.GIF);', 'assets/style.css', null);
    expect(transformedText).to.eql('background-image:url(data:image/gif;base64,);');
  });

  it('should encode a JPG', function() {
    var transformedText = transform('background-image:url(images/sprite.jpeg);', 'assets/style.css', null);
    expect(transformedText).to.eql('background-image:url(data:image/jpeg;base64,);');
  });
});



